let col = 0;
$(".contact__body-form-main-btn").on("click", function (e) {
  $(".input").each(function (e) {
    0 == $(this).val().length
      ? $(this).addClass("error")
      : (col++, $(this).removeClass("error"));
  }),
    3 == col &&
      ($(this).attr("type", "submit"), (window.location.href = "good.html"));
}),
  $(window).on("scroll", function (e) {
    $(window).scrollTop() >= 200
      ? $(".telegram").addClass("active")
      : $(".telegram").removeClass("active");
  }),
  SmoothScroll({
    animationTime: 600,
    stepSize: 45,
    accelerationDelta: 30,
    accelerationMax: 1,
    keyboardSupport: !0,
    arrowScroll: 100,
    pulseAlgorithm: !0,
    pulseScale: 4,
    pulseNormalize: 1,
    touchpadSupport: !0,
  });
